import React, { useState, useEffect } from 'react';
import api from '../../services/api';
import { useParams } from 'react-router-dom'
import { Container, Owner, Loading, BackButton, IssuesList, PageActions, FilterField } from './styles'
import { FaArrowLeft } from 'react-icons/fa'

export default function Repositorio(){
  const { repositorioParams } = useParams();

  const [repositorio, setRepositorio] = useState({});
  const [issues, setIssues] = useState([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);
  const [filter, setFilter] = useState('open');
  
  useEffect(()=> {
    
    async function load(){
      const nomeRepo = decodeURIComponent(repositorioParams);

      const [repositorioData, issuesData] = await Promise.all([//vai executar as duas requisições ao mesmo tempo
        api.get(`/repos/${nomeRepo}`),
        api.get(`/repos/${nomeRepo}/issues`, {
          params:{
            state: 'all',
            per_page: 5
          }
        })
      ]);

      setRepositorio(repositorioData.data);
      setIssues(issuesData.data);
      setLoading(false);
    }
    load();
  }, [repositorioParams]);

  useEffect(()=> {
    async function loadIssue(){
      const nomeRepo = decodeURIComponent(repositorioParams);

      const response = await api.get(`/repos/${nomeRepo}/issues`,{
        params:{
          state: filter,
          page: page,
          per_page: 5,
        },
      });
      setIssues(response.data)
    }

    loadIssue();
  }, [page, filter])

  function handlePage(action){
    setPage(action === 'back' ? page - 1 : page + 1)
  }

  if(loading){
    return(
      <Loading>
        <h1>Carregando...</h1>
      </Loading>
    )
  }

  function handleIssuesFilter(value){
    setFilter(value)
    setPage(1)
  }

  return(
    <Container>
      <BackButton to="/">
        <FaArrowLeft color="#000" size={35}/>
      </BackButton>
      <Owner>
        <img
         src={repositorio.owner.avatar_url}
         alt={repositorio.owner.login}
        />
        <h1>{repositorio.name}</h1>
        <p>{repositorio.description}</p>
      </Owner>

      <FilterField>
        <strong>Filtro: </strong>
        <button type="button"
         onClick={()=> handleIssuesFilter('open')}
         >
          Em Aberto
        </button>

        <button type="button"
         onClick={()=> handleIssuesFilter('closed')}
         >
          Fechado
        </button>

        <button type="button"
         onClick={()=> handleIssuesFilter('all')}
         >
          Todos
        </button>
      </FilterField>

      <IssuesList>
        {issues.map(issue =>(
          <li key={String(issue.id)}>
            <img src={issue.user.avatar_url} alt={issue.user.login} />

            <div>
              <p>{issue.state}</p>
              <strong>
                <a href={issue.html_url} target="blank">{issue.title}</a>
                {issue.labels.map(label =>(
                  <span key={String(label.id)}>
                    {label.name}
                  </span>
                ))}
              </strong>
              <p>{issue.user.login}</p>
            </div>
          </li>
        ))}
      </IssuesList>

      <PageActions>
        <button
         type="button" 
         onClick={()=> handlePage('back')}
         disabled={page < 2}
        >
          Voltar
        </button>
        
        <span>Pagina {page}</span>

        <button
         type="button"
         onClick={()=> handlePage('next')}
        >
          Próxima
        </button>
      </PageActions>
    </Container>
  )
}
